import React, { Component } from "react";
import Cookies from "js-cookie";
import Popup from "reactjs-popup";


class NewTransaction extends Component {

state = {
      name: null,
      amount: null,
      sharedBy: [],
      sharedPeople: this.props.sharedPeople,
      comment: null
  };






  handleChange(event){

  }

  handleSubmit(event){

  }

  log = () => {
    console.log(this.state.sharedPeople);
  }


  render() {
    const boxes = []
    var person = null
    for( person of this.state.sharedPeople ){
    boxes.push(<label>{person}<input type="checkbox"></input></label>)
    }
    return (
      <Popup trigger={<button style={{
        position: 'absolute', left: '50%', top: '50%',
        transform: 'translate(-50%, -50%)'
      }}>Add A Transaction</button>} key={this.props.sharedPeople}>
        {close => (
          <div>
            <div>
              <form>
                <p>
                <label>
                  Paid By:
                  {boxes}
                </label>
                </p>
                <p>
                <label>
                  Amount:
                  <input type="text" pattern="[0-9]*" onChange={this.handleChange}></input>
                </label>
                </p>
                <p>
                <label>
                  Shared By:
                  {boxes}
                </label>
                </p>
                <p>
                <label>
                  Comment:
                  <input type="text" onChange={this.handleChange}></input>
                </label>
                </p>
                <input type="submit" value="Submit" />
              </form>
            </div>
            <div>
              <button className="close" onClick={close} position="bottom right">
                close
              </button>
              <button onClick={() => this.log()}>
                log
              </button>
            </div>

          </div>
        )}
      </Popup>
    );
  }
}

export default NewTransaction;
