import React, { Component } from "react";
import Cookies from "js-cookie";
import NewTransaction from "./NewTransaction";
import Table from "react-table-6";
import 'react-table-6/react-table.css'
import axios from 'axios';

class newRecord extends Component {

  constructor() {
    super();

    this.state = {
    owner: Cookies.get("name"),
    date: null,
    transactions: [],
    currentName: null,
    sharedBy: [Cookies.get("name")],
    numShared: 1,
    intervalIsSet: false,
    errors: {}
  };

}

  componentDidMount() {
    this.getTransactionFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }

  getTransactionFromDb = () => {
    fetch('http://localhost:3001/api/getTransaction')
      .then((transactions) => transactions.json())
      .then((res) => {
        this.setState({ transactions: res.transactions});
        });
      console.log(this.state.transactions);
  };

  addSharedBy = (name) => {
    var temp = [...this.state.sharedBy];
    temp.push(name);
    this.setState({sharedBy: temp});
    var num = this.state.numShared + 1;
    this.setState({numShared:num});
    console.log(this.state.sharedBy);
    console.log(this.state.numShared);
    console.log(this.state.currentName);
    console.log(this.state.owner);
  };

  createRecord = (event) =>{
      axios.post('http://localhost:3001/api/putRecord', {
        owner: this.state.owner,
        date: this.state.date,
        transactions: [],
    });

      Cookies.set('sharer', this.state.sharedBy, {path: ''});
  };



  render() {
    const columns = [
            {
              Header: 'Payer',
              accessor: 'payer',
            },
            {
              Header: 'Amount',
              accessor: 'amount',
            },
            {
              Header: 'Shared With',
              accessor: 'displayShared'
            },
            {
              Header: 'Comment',
              accessor: 'comment',
            },
          ]

    return (
      <div>

          Creating new record for {this.state.owner}...
          <label visible='false'>
            Shared with:{this.state.sharedBy}
            <input type="text" placeholder="name" onChange={(e) => this.setState({ currentName: e.target.value })}></input>
            <button onClick={() =>this.addSharedBy(this.state.currentName)}>Add Sharer</button>
            <input type="text" placeholder="date" onChange={(e) => this.setState({ date: e.target.value})}></input>
            <button onClick={(e) => this.createRecord(e)}>Create New Record</button>
          </label>

        <div>
          <Table columns={columns} data={this.state.transactions} />
        </div>    
        <div>
        <NewTransaction sharedPeople={this.state.sharedBy} num={this.state.numShared}  />
        </div>
      </div>
    );
  }
}

export default newRecord;
