import React, { Component } from "react";
import Cookies from "js-cookie";
import Popup from "reactjs-popup";


class NewTransaction extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: null,
      amount: null,
      sharedBy: [],
      sharedPeople: this.props.sharedPeople,
      comment: null
    };

    this.isChecked = this.isChecked.bind(this);

  }

  isChecked(event){
    return (event.target.value === this.state.name);
  }







  handleSubmit(event) {

  }

  log = () => {
    console.log(this.state.name);
  }


  render() {
    return (
      <Popup trigger={<button style={{
        position: 'absolute', left: '50%', top: '50%',
        transform: 'translate(-50%, -50%)'
      }}>Add A Transaction</button>} key={this.props.sharedPeople}>
        {close => (
          <div>
            <div>
              <form>
                <p>
                  <label>
                    Paid By:
                  {this.state.sharedPeople.map((person) =>
                      <label key={person}>{person}<input type="radio" value={person} checked={this.isChecked} onChange={(e) => this.setState({name:e.target.value})}></input></label>
                    )}
                  </label>
                </p>
                <p>
                  <label>
                    Amount:
                  <input type="text" pattern="[0-9]*" onChange={(e) => this.setState({ amount: e.target.value })}></input>
                  </label>
                </p>
                <p>
                  <label>
                    Shared By:
                  {this.state.sharedPeople.map((person) =>
                      <label key={person}>{person}<input type="checkbox"></input></label>
                    )}
                  </label>
                </p>
                <p>
                  <label>
                    Comment:
                  <input type="text" onChange={(e) => this.setState({ comment: e.target.value })}></input>
                  </label>
                </p>
                <input type="submit" value="Submit" />
              </form>
            </div>
            <div>
              <button className="close" onClick={close} position="bottom right">
                close
              </button>
              <button onClick={() => this.log()}>
                log
              </button>
            </div>

          </div>
        )}
      </Popup>
    );
  }
}

export default NewTransaction;
